import React, { Component ,useState , useEffect } from "react";
import Chart from "react-apexcharts";
import {
    Streamlit,
    StreamlitComponentBase,
    withStreamlitConnection,
  } from "streamlit-component-lib"


  function EventCallback (event :any , chartContext :any , config :any ) {

    Streamlit.setComponentValue(config.selectedDataPoints)

  }


  function UpdateOption(option : any ){

    console.log("rerender : " , option)

    var events = option.chart.events 

    var eventsReplace = {}

    Object.keys(events).forEach(( k : any  ) => {

            eventsReplace = { ...eventsReplace , [k] :  (event :any , chartContext :any , config :any) =>  {   

                // console.log(index)

                EventCallback(event   , chartContext  , config  )  } }

        }
        )

    events = eventsReplace



    // option.chart.events  = events

    option.chart.type ="line"

    return option
}

function App(props:any) {

    // const [options, setOptions] = useState( op  )
    const [series, setSeries] = useState( props.chart_dict.series )
    const [index, setIndex] = useState( props.chart_dict.index )

    var  options = props.chart_dict.options 

    var events = options.chart.events 

    var eventsReplace = {}

    Object.keys(events).forEach(( k : any  ) => {

            eventsReplace = { ...eventsReplace , [k] :  (event :any , chartContext :any , config :any) =>  {   

                var dataPoint =config.selectedDataPoints

                dataPoint = dataPoint.filter((k:any) => k== null )

                var dataPointMap = dataPoint.map( (k :any   , i :any )=>    { 
                    return {
                        columns  : index["columns"][i],  
                        index :  index["index"][dataPoint[i]]
                    }

                  }  )


            } }
        }
        )

    options.chart.events  = eventsReplace

    return (
        <div className="app">
        <div className="row">
          <div className="mixed-chart">

            {/* <div>  {t}</div> */}
            <Chart
              options={options}
              series={series} 
              type= {options.chart.type}
              width= {options.chart.width}
              height= {options.chart.height}
            />
          </div>
        </div>
      </div>
    );
  }



export default App;
