//       dataPointSelection : {type :  'mousedown' , return : ['seriesIndex' , 'dataPointIndex' ,  'selectedDataPoints']  }


//  }



  function EventCallback (event :any , chartContext :any , config :any , eventType :any  ) {

    // console.log(event)

    // // console.log(chartContext)
    // console.log(config)

      // var eventType = event.type 

      Streamlit.setComponentValue( { [eventType] :  {dataPointIndex : config.dataPointIndex  , seriesIndex : config.seriesIndex  ,selectedDataPoints :  "selectedDataPoints" in  config ?config["selectedDataPoints"] : null  } })   

      // if (eventType == "mousedown") {

      //   var dataPoint =config.selectedDataPoints

      //   var dataPointMap = dataPoint.map( (k :any   , i :any )=>    { 

      //     if ( k !== null ) {

      //         // var dataPoindexList = dataPoint[i]

      //         // dataPoindexList

      //         return {
      //             columns  : [index["columns"][i]],  
      //             index :  [index["index"][dataPoint[i]]]
      //         }

      //     }

      //   }  );

      //   dataPointMap = dataPointMap.filter((k:any) => k!= null )

      //   Streamlit.setComponentValue(dataPointMap)   

      // } else {


      //   var dataSeries =config.seriesIndex 

      //   var dataPoint = config.dataPointIndex

      //   Streamlit.setComponentValue([ {columns : [index["columns"][dataSeries] ] ,index : [index["index"][dataPoint] ]}])  

      // }



  }




function StreamlitCharts(props:any) {

    // const [options, setOptions] = useState( props.chart_dict.options   )

    const options = useRef(props.chart_dict.options)


    // const [series, setSeries] = useState( props.chart_dict.series )
    // const [index, setIndex] = useState( props.chart_dict.index )

    // const app =  useRef<HTMLInputElement>(null)

    // var options = props.chart_dict.options



    useEffect(  () => {


      if (props.chart_dict.options === options )


      var  newOptions = props.chart_dict.options

      var eventsCopy = {...newOptions.chart.events}


      Object.keys(eventsCopy).forEach((k) => {
        eventsCopy[k]  = eventsCopy[k] ? (event :any , chartContext :any , config :any) => { EventCallback(event , chartContext , config   , k )   } : null
       })


       var FroamterCheck = newOptions

       Object.keys(FroamterCheck).map( (k , i ) => {

          if (k in {xaxis:[] , yaxis : [] }){

              if ( "labels" in FroamterCheck[k] ) {

                if ( "formatter" in FroamterCheck[k]["labels"] ) {

                  FroamterCheck[k]["labels"]["formatter"] = eval(FroamterCheck[k]["labels"]["formatter"])
                }
              }

            }
        } )

      //  options  =   {...options , chart : { ...options.chart , events  : eventsCopy } }


      setOptions({...newOptions , chart : { ...newOptions.chart , events  : eventsCopy }})

      console.log(props.chart_dict.options.chart.events )


      // console.log(options)


    } , [props.chart_dict.options ]
    )


    // useEffect(()=> {}  , [])





    // useEffect(() => {

    //     var eventsCopy = {...options.chart.events}

    //     Object.keys(eventsCopy).forEach((k) => {

    //       eventsCopy[k]  = eventsCopy[k] ? (event :any , chartContext :any , config :any) => { EventCallback(event , chartContext , config , index)   } : null

    //      })

    //     setOptions( {...options , chart : { ...options.chart , events  : eventsCopy } } )  

    // },[options.chart.events])





    return (
        <div className="app" >
        <div className="row">
          <div className="mixed-chart">

            <Chart
              options={options}
              series={props.chart_dict.series} 
              type= {options.chart.type}
              width= {props.width}
              height= {options.chart.height}
            />
          </div>
        </div>
      </div>
    );
  }

export default StreamlitCharts;
