        }
        )
        options.chart.events = eventsReplace

        options.chart.type = "line"

        setOptions(options)

        console.log(options.chart.type)

    } , [options] ) 


    console.log(options.chart.type)


    return (
        <div className="app">
        <div className="row">
          <div className="mixed-chart">
            <Chart
              options={options}
              series={series} 
              type= {options.chart.type}
              width= {options.chart.width}
              height= {options.chart.height}
            />
          </div>
        </div>
      </div>
    );
  }



export default App;
