import React, { Component ,useState } from "react";
import Chart from "react-apexcharts";
import {
    Streamlit,
    StreamlitComponentBase,
    withStreamlitConnection,
  } from "streamlit-component-lib"


  function EventCallback (event :any , chartContext :any , config :any ) {

    // console.log(config)

    Streamlit.setComponentValue(config.dataPointIndex)

  }




function App(props:any) {

    // console.log(props.name.options.chart.events)


    var config = props.name 

    var events = config.options.chart.events 

    var eventsReplace = {}

    Object.keys(events).forEach(( k : any  ) => {


        eventsReplace = { ...eventsReplace , dataPointSelection : (e :any  , c : any  , config :any  ) =>  EventCallback(e   , c   , config ) }

    })

    config.options.chart.events = eventsReplace


    console.log(config)



    // Declare a new state variable, which we'll call "count"
    const [count, setCount] = useState(config)

    return (
        <div className="app">
        <div className="row">
          <div className="mixed-chart">
            <Chart
              options={count.options}
              series={count.series} 
              type="bar"
              width="500"
            />
          </div>
        </div>
      </div>
    );
  }



export default App;
