import React, { Component ,useState } from "react";
import Chart from "react-apexcharts";
import {
    Streamlit,
    StreamlitComponentBase,
    withStreamlitConnection,
  } from "streamlit-component-lib"


  function EventCallback (event :any , chartContext :any , config :any ) {

    console.log(config)

    Streamlit.setComponentValue(config.selectedDataPoints)

  }




function App(props:any) {

    var config = props.chart_dict 

    var events = config.options.chart.events 

    var eventsReplace = {}

    Object.keys(events).forEach(( k : any  ) => {

        eventsReplace = { ...eventsReplace , [k] : (e :any  , c : any  , config :any  ) =>  EventCallback(e   , c   , config ) }

    })

    config.options.chart.events = eventsReplace


    // Declare a new state variable, which we'll call "count"
    const [count, setCount] = useState(config)

    return (
        <div className="app">
        <div className="row">
          <div className="mixed-chart">
            <Chart
              options={count.options}
              series={count.series} 
              type= {count.options.chart.type}
              width= {count.options.chart.width}
              height= {count.options.chart.height}
            />
          </div>
        </div>
      </div>
    );
  }



export default App;
